    "作者朝代": "清代",
    "诗名": "长相思·山一程",
    "诗词": "山一程，水一程，身向榆关那畔行，夜深千帐灯。风一更，雪一更，聒碎乡心梦不成，故园无此声。"
} ]);
db.getCollection("ci").insert([ {
    _id: ObjectId("61a8cd8c4a506fc7c2b8c24d"),
    "作者名": "辛弃疾",
    "作者朝代": "宋代",
    "诗名": "南乡子·登京口北固亭有怀",
    "诗词": "何处望神州？满眼风光北固楼。千古兴亡多少事？悠悠。不尽长江滚滚流。年少万兜鍪，坐断东南战未休。天下英雄谁敌手？曹刘。生子当如孙仲谋。"
} ]);
db.getCollection("ci").insert([ {
    _id: ObjectId("61a8cd8c4a506fc7c2b8c24e"),
    "作者名": "李清照",
    "作者朝代": "宋代",
    "诗名": "醉花阴·薄雾浓云愁永昼",
    "诗词": "薄雾浓云愁永昼，瑞脑销金兽。佳节又重阳，玉枕纱厨，半夜凉初透。(厨 通：橱；销金兽 一作：消金兽)东篱把酒黄昏后，有暗香盈袖。莫道不销魂，帘卷西风，人比黄花瘦。(人比 一作：人似)"
} ]);
db.getCollection("ci").insert([ {
    _id: ObjectId("61a8cd8c4a506fc7c2b8c24f"),
    "作者名": "辛弃疾",
    "作者朝代": "宋代",
    "诗名": "清平乐·村居",
    "诗词": "茅檐低小，溪上青青草。醉里吴音相媚好，白发谁家翁媪？大儿锄豆溪东，中儿正织鸡笼。最喜小儿亡赖，溪头卧剥莲蓬。(亡 同：无)"
} ]);
db.getCollection("ci").insert([ {
    _id: ObjectId("61a8cd8c4a506fc7c2b8c250"),
    "作者名": "李煜",
    "作者朝代": "五代",
    "诗名": "相见欢·无言独上西楼",
    "诗词": "无言独上西楼，月如钩。寂寞梧桐深院锁清秋。剪不断，理还乱，是离愁。别是一般滋味在心头。(一般 一作：一番)"
} ]);
db.getCollection("ci").insert([ {
    _id: ObjectId("61a8cd8c4a506fc7c2b8c251"),
    "作者名": "李煜",
    "作者朝代": "五代",
    "诗名": "浪淘沙令·帘外雨潺潺",
    "诗词": "帘外雨潺潺，春意阑珊。罗衾不耐五更寒。梦里不知身是客，一晌贪欢。独自莫凭栏，无限江山。别时容易见时难。流水落花春去也，天上人间。"
} ]);
db.getCollection("ci").insert([ {
    _id: ObjectId("61a8cd8c4a506fc7c2b8c252"),
    "作者名": "杨慎",
    "作者朝代": "明代",
    "诗名": "临江仙·滚滚长江东逝水",
    "诗词": "滚滚长江东逝水，浪花淘尽英雄。是非成败转头空。青山依旧在，几度夕阳红。白发渔樵江渚上，惯看秋月春风。一壶浊酒喜相逢。古今多少事，都付笑谈中。"
} ]);
db.getCollection("ci").insert([ {
    _id: ObjectId("61a8cd8c4a506fc7c2b8c253"),
    "作者名": "李清照",
    "作者朝代": "宋代",
    "诗名": "渔家傲·天接云涛连晓雾",
    "诗词": "天接云涛连晓雾，星河欲转千帆舞。仿佛梦魂归帝所。闻天语，殷勤问我归何处。我报路长嗟日暮，学诗谩有惊人句。九万里风鹏正举。风休住，蓬舟吹取三山去！"
} ]);
db.getCollection("ci").insert([ {
    _id: ObjectId("61a8cd8c4a506fc7c2b8c254"),
    "作者名": "李之仪",
    "作者朝代": "宋代",
    "诗名": "卜算子·我住长江头",
    "诗词": "我住长江头，君住长江尾。日日思君不见君，共饮长江水。 此水几时休，此恨何时已。只愿君心似我心，定不负相思意。"
} ]);
db.getCollection("ci").insert([ {
    _id: ObjectId("61a8cd8c4a506fc7c2b8c255"),
    "作者名": "纳兰性德",
    "作者朝代": "清代",
    "诗名": "浣溪沙·谁念西风独自凉",
    "诗词": "谁念西风独自凉，萧萧黄叶闭疏窗，沉思往事立残阳。被酒莫惊春睡重，赌书消得泼茶香，当时只道是寻常。"
} ]);
db.getCollection("ci").insert([ {
    _id: ObjectId("61a8cd8c4a506fc7c2b8c256"),
    "作者名": "范仲淹",
    "作者朝代": "宋代",
    "诗名": "苏幕遮·怀旧",
    "诗词": "碧云天，黄叶地，秋色连波，波上寒烟翠。山映斜阳天接水，芳草无情，更在斜阳外。黯乡魂，追旅思，夜夜除非，好梦留人睡。明月楼高休独倚，酒入愁肠，化作相思泪。(留人睡 一作：留人醉)"
} ]);
db.getCollection("ci").insert([ {
    _id: ObjectId("61a8cd8c4a506fc7c2b8c257"),
    "作者名": "苏轼",
    "作者朝代": "宋代",
    "诗名": "临江仙·送钱穆父",
    "诗词": "一别都门三改火，天涯踏尽红尘。依然一笑作春温。无波真古井，有节是秋筠。惆怅孤帆连夜发，送行淡月微云。尊前不用翠眉颦。人生如逆旅，我亦是行人。(尊 通：樽)"
} ]);
db.getCollection("ci").insert([ {
    _id: ObjectId("61a8cd8c4a506fc7c2b8c258"),
    "作者名": "白居易",
    "作者朝代": "唐代",
    "诗名": "忆江南",
    "诗词": "江南好，风景旧曾谙。日出江花红胜火，春来江水绿如蓝。能不忆江南？"
} ]);
db.getCollection("ci").insert([ {
    _id: ObjectId("61a8cd8c4a506fc7c2b8c259"),
    "作者名": "苏轼",
    "作者朝代": "宋代",
    "诗名": "望江南·超然台作",
    "诗词": "春未老，风细柳斜斜。试上超然台上看，半壕春水一城花。烟雨暗千家。(看 一作：望)寒食后，酒醒却咨嗟。休对故人思故国，且将新火试新茶。诗酒趁年华。"
} ]);
db.getCollection("ci").insert([ {
    _id: ObjectId("61a8cd8c4a506fc7c2b8c25a"),
    "作者名": "苏轼",
    "作者朝代": "宋代",
    "诗名": "西江月·世事一场大梦",
    "诗词": "世事一场大梦，人生几度秋凉？夜来风叶已鸣廊。看取眉头鬓上。(秋凉 一作：新凉)酒贱常愁客少，月明多被云妨。中秋谁与共孤光。把盏凄然北望。 "
} ]);
db.getCollection("ci").insert([ {
    _id: ObjectId("61a8cd8c4a506fc7c2b8c25b"),
    "作者名": "欧阳修",
    "作者朝代": "宋代",
    "诗名": "蝶恋花·庭院深深深几许",
    "诗词": "庭院深深深几许，杨柳堆烟，帘幕无重数。玉勒雕鞍游冶处，楼高不见章台路。雨横风狂三月暮，门掩黄昏，无计留春住。泪眼问花花不语，乱红飞过秋千去。"
} ]);
db.getCollection("ci").insert([ {
    _id: ObjectId("61a8cd8c4a506fc7c2b8c25c"),
    "作者名": "辛弃疾",
    "作者朝代": "宋代",
    "诗名": "水龙吟·登建康赏心亭",
    "诗词": "楚天千里清秋，水随天去秋无际。遥岑远目，献愁供恨，玉簪螺髻。落日楼头，断鸿声里，江南游子。把吴钩看了，栏杆拍遍，无人会，登临意。(栏杆 一作：阑干)休说鲈鱼堪脍，尽西风，季鹰归未？求田问舍，怕应羞见，刘郎才气。可惜流年，忧愁风雨，树犹如此！倩何人唤取，红巾翠袖，揾英雄泪！"
} ]);
db.getCollection("ci").insert([ {
    _id: ObjectId("61a8cd8c4a506fc7c2b8c25d"),
    "作者名": "辛弃疾",
    "作者朝代": "宋代",
    "诗名": "菩萨蛮·书江西造口壁",
    "诗词": "郁孤台下清江水，中间多少行人泪。西北望长安，可怜无数山。青山遮不住，毕竟东流去。江晚正愁予，山深闻鹧鸪。 (愁予 一作：愁余)"
} ]);
db.getCollection("ci").insert([ {
    _id: ObjectId("61a8cd8c4a506fc7c2b8c25e"),
    "作者名": "欧阳修",
    "作者朝代": "宋代",
    "诗名": "生查子·元夕",
    "诗词": "去年元夜时，花市灯如昼。月上柳梢头，人约黄昏后。今年元夜时，月与灯依旧。不见去年人，泪湿春衫袖。(湿 一作：满)"
} ]);
db.getCollection("ci").insert([ {
    _id: ObjectId("61a8cd8c4a506fc7c2b8c25f"),
    "作者名": "柳永",
    "作者朝代": "宋代",
    "诗名": "望海潮·东南形胜",
    "诗词": "东南形胜，三吴都会，钱塘自古繁华。烟柳画桥，风帘翠幕，参差十万人家。云树绕堤沙，怒涛卷霜雪，天堑无涯。市列珠玑，户盈罗绮，竞豪奢。重湖叠巘清嘉，有三秋桂子，十里荷花。羌管弄晴，菱歌泛夜，嬉嬉钓叟莲娃。千骑拥高牙，乘醉听箫鼓，吟赏烟霞。异日图将好景，归去凤池夸。"
} ]);
db.getCollection("ci").insert([ {
    _id: ObjectId("61a8cd8c4a506fc7c2b8c260"),
    "作者名": "李煜",
    "作者朝代": "五代",
    "诗名": "相见欢·林花谢了春红",
    "诗词": "林花谢了春红，太匆匆。无奈朝来寒雨晚来风。胭脂泪，相留醉，几时重。自是人生长恨水长东。(相留 一作：留人)"
} ]);
db.getCollection("ci").insert([ {
    _id: ObjectId("61a8cd8c4a506fc7c2b8c261"),
    "作者名": "晏殊",
    "作者朝代": "宋代",
    "诗名": "蝶恋花·槛菊愁烟兰泣露",
    "诗词": "槛菊愁烟兰泣露，罗幕轻寒，燕子双飞去。明月不谙离恨苦，斜光到晓穿朱户。(双飞去 一作：双来去；离恨苦 一作：离别苦)昨夜西风凋碧树，独上高楼，望尽天涯路。欲寄彩笺兼尺素，山长水阔知何处？"
} ]);
db.getCollection("ci").insert([ {
    _id: ObjectId("61a8cd8c4a506fc7c2b8c262"),
    "作者名": "唐寅",
    "作者朝代": "明代",
    "诗名": "一剪梅·雨打梨花深闭门",
    "诗词": "雨打梨花深闭门，孤负青春，虚负青春。赏心乐事共谁论？花下销魂，月下销魂。(孤负 一作：忘了；虚负 一作：误了)愁聚眉峰尽日颦，千点啼痕，万点啼痕。晓看天色暮看云，行也思君，坐也思君。"
} ]);
db.getCollection("ci").insert([ {
    _id: ObjectId("61a8cd8c4a506fc7c2b8c263"),
    "作者名": "苏轼",
    "作者朝代": "宋代",
    "诗名": "临江仙·夜归临皋",
    "诗词": "夜饮东坡醒复醉，归来仿佛三更。家童鼻息已雷鸣。敲门都不应，倚杖听江声。 长恨此身非我有，何时忘却营营。夜阑风静縠纹平。小舟从此逝，江海寄余生。(余 一作：馀)"
} ]);
db.getCollection("ci").insert([ {
    _id: ObjectId("61a8cd8c4a506fc7c2b8c264"),
    "作者名": "刘禹锡",
    "作者朝代": "唐代",
    "诗名": "浪淘沙·其一",
    "诗词": "九曲黄河万里沙，浪淘风簸自天涯。如今直上银河去，同到牵牛织女家。"
} ]);
db.getCollection("ci").insert([ {
    _id: ObjectId("61a8cd8c4a506fc7c2b8c265"),
    "作者名": "白居易",
    "作者朝代": "唐代",
    "诗名": "忆江南词三首",
    "诗词": "江南好，风景旧曾谙。日出江花红胜火，春来江水绿如蓝。能不忆江南？江南忆，最忆是杭州。山寺月中寻桂子，郡亭枕上看潮头。何日更重游？江南忆，其次忆吴宫。吴酒一杯春竹叶，吴娃双舞醉芙蓉。早晚复相逢？"
} ]);
db.getCollection("ci").insert([ {
    _id: ObjectId("61a8cd8c4a506fc7c2b8c266"),
    "作者名": "张志和",
    "作者朝代": "唐代",
    "诗名": "渔歌子·西塞山前白鹭飞",
    "诗词": "西塞山前白鹭飞，桃花流水鳜鱼肥。青箬笠，绿蓑衣，斜风细雨不须归。"
} ]);
db.getCollection("ci").insert([ {
    _id: ObjectId("61a8cd8c4a506fc7c2b8c267"),
    "作者名": "蒋捷",
    "作者朝代": "宋代",
    "诗名": "虞美人·听雨",
    "诗词": "少年听雨歌楼上，红烛昏罗帐。壮年听雨客舟中，江阔云低、断雁叫西风。 而今听雨僧庐下，鬓已星星也。悲欢离合总无情，一任阶前、点滴到天明。"
} ]);
db.getCollection("ci").insert([ {
    _id: ObjectId("61a8cd8c4a506fc7c2b8c268"),
    "作者名": "唐婉",
    "作者朝代": "宋代",
    "诗名": "钗头凤·世情薄",
    "诗词": "世情薄，人情恶，雨送黄昏花易落。晓风干，泪痕残。欲笺心事，独语斜阑。难，难，难！人成各，今非昨，病魂常似秋千索。角声寒，夜阑珊。怕人寻问，咽泪装欢。瞒，瞒，瞒！(装 通：妆)"
} ]);
db.getCollection("ci").insert([ {
    _id: ObjectId("61a8cd8c4a506fc7c2b8c269"),
    "作者名": "王观",
    "作者朝代": "宋代",
    "诗名": "卜算子·送鲍浩然之浙东",
    "诗词": "水是眼波横，山是眉峰聚。欲问行人去那边？眉眼盈盈处。才始送春归，又送君归去。若到江南赶上春，千万和春住。"
} ]);
db.getCollection("ci").insert([ {
    _id: ObjectId("61a8cd8c4a506fc7c2b8c26a"),
    "作者名": "陆游",
    "作者朝代": "宋代",
    "诗名": "诉衷情·当年万里觅封侯",
    "诗词": "当年万里觅封侯，匹马戍梁州。关河梦断何处？尘暗旧貂裘。胡未灭，鬓先秋，泪空流。此生谁料，心在天山，身老沧洲。"
} ]);
db.getCollection("ci").insert([ {
    _id: ObjectId("61a8cd8c4a506fc7c2b8c26b"),
    "作者名": "晏几道",
    "作者朝代": "宋代",
    "诗名": "临江仙·梦后楼台高锁",
    "诗词": "梦后楼台高锁，酒醒帘幕低垂。去年春恨却来时。落花人独立，微雨燕双飞。 记得小蘋初见，两重心字罗衣。琵琶弦上说相思。当时明月在，曾照彩云归。(蘋 通：苹)"
} ]);
db.getCollection("ci").insert([ {
    _id: ObjectId("61a8cd8c4a506fc7c2b8c26c"),
    "作者名": "元好问",
    "作者朝代": "金朝",
    "诗名": "摸鱼儿·雁丘词",
    "诗词": "乙丑岁赴试并州，道逢捕雁者云：“今旦获一雁，杀之矣。其脱网者悲鸣不能去，竟自投于地而死。”予因买得之，葬之汾水之上，垒石为识，号曰“雁丘”。同行者多为赋诗，予亦有《雁丘词》。旧所作无宫商，今改定之。问世间，情是何物，直教生死相许？天南地北双飞客，老翅几回寒暑。欢乐趣，离别苦，就中更有痴儿女。君应有语：渺万里层云，千山暮雪，只影向谁去？横汾路，寂寞当年箫鼓，荒烟依旧平楚。招魂楚些何嗟及，山鬼暗啼风雨。天也妒，未信与，莺儿燕子俱黄土。千秋万古，为留待骚人，狂歌痛饮，来访雁丘处。 "
} ]);
db.getCollection("ci").insert([ {
    _id: ObjectId("61a8cd8c4a506fc7c2b8c26d"),
    "作者名": "柳永",
    "作者朝代": "宋代",
    "诗名": "八声甘州·对潇潇暮雨洒江天",
    "诗词": "对潇潇暮雨洒江天，一番洗清秋。渐霜风凄紧，关河冷落，残照当楼。是处红衰翠减，苒苒物华休。唯有长江水，无语东流。(凄紧 一作：凄惨) 不忍登高临远，望故乡渺邈，归思难收。叹年来踪迹，何事苦淹留。想佳人妆楼颙望，误几回、天际识归舟。争知我，倚阑杆处，正恁凝愁！(阑 一作：栏) "
} ]);
db.getCollection("ci").insert([ {
    _id: ObjectId("61a8cd8c4a506fc7c2b8c26e"),
    "作者名": "姜夔",
    "作者朝代": "宋代",
    "诗名": "扬州慢·淮左名都",
    "诗词": "淳熙丙申至日，予过维扬。夜雪初霁，荠麦弥望。入其城，则四顾萧条，寒水自碧，暮色渐起，戍角悲吟。予怀怆然，感慨今昔，因自度此曲。千岩老人以为有《黍离》之悲也。淮左名都，竹西佳处，解鞍少驻初程。过春风十里，尽荠麦青青。自胡马窥江去后，废池乔木，犹厌言兵。渐黄昏，清角吹寒，都在空城。 杜郎俊赏，算而今重到须惊。纵豆蔻词工，青楼梦好，难赋深情。二十四桥仍在，波心荡，冷月无声。念桥边红药，年年知为谁生？"
} ]);
db.getCollection("ci").insert([ {
    _id: ObjectId("61a8cd8c4a506fc7c2b8c26f"),
    "作者名": "苏轼",
    "作者朝代": "宋代",
    "诗名": "定风波·南海归赠王定国侍人寓娘",
    "诗词": "王定国歌儿曰柔奴，姓宇文氏，眉目娟丽，善应对，家世住京师。定国南迁归，余问柔：“广南风土， 应是不好？”柔对曰：“此心安处，便是吾乡。”因为缀词云。常羡人间琢玉郎，天应乞与点酥娘。尽道清歌传皓齿，风起，雪飞炎海变清凉。(天应乞与 一作：天教分付； 尽道 一作：自作)万里归来颜愈少。微笑，笑时犹带岭梅香。试问岭南应不好，却道：此心安处是吾乡。"
} ]);
db.getCollection("ci").insert([ {
    _id: ObjectId("61a8cd8c4a506fc7c2b8c270"),
    "作者名": "苏轼",
    "作者朝代": "宋代",
    "诗名": "水龙吟·次韵章质夫杨花词",
    "诗词": "似花还似非花，也无人惜从教坠。抛家傍路，思量却是，无情有思。萦损柔肠，困酣娇眼，欲开还闭。梦随风万里，寻郎去处，又还被莺呼起。不恨此花飞尽，恨西园、落红难缀。晓来雨过，遗踪何在？一池萍碎。春色三分，二分尘土，一分流水。细看来，不是杨花，点点是离人泪。"
} ]);
db.getCollection("ci").insert([ {
    _id: ObjectId("61a8cd8c4a506fc7c2b8c271"),
    "作者名": "苏轼",
    "作者朝代": "宋代",
    "诗名": "浣溪沙·细雨斜风作晓寒",
    "诗词": "元丰七年十二月二十四日，从泗州刘倩叔游南山细雨斜风作晓寒，淡烟疏柳媚晴滩。入淮清洛渐漫漫。雪沫乳花浮午盏，蓼茸蒿笋试春盘。人间有味是清欢。"
} ]);
db.getCollection("ci").insert([ {
    _id: ObjectId("61a8cd8c4a506fc7c2b8c272"),
    "作者名": "温庭筠",
    "作者朝代": "唐代",
    "诗名": "菩萨蛮·小山重叠金明灭",
    "诗词": "小山重叠金明灭，鬓云欲度香腮雪。懒起画蛾眉，弄妆梳洗迟。照花前后镜，花面交相映。新帖绣罗襦，双双金鹧鸪。(帖 一作：贴)"
} ]);
db.getCollection("ci").insert([ {
    _id: ObjectId("61a8cd8c4a506fc7c2b8c273"),
    "作者名": "秦观",
    "作者朝代": "宋代",
    "诗名": "浣溪沙·漠漠轻寒上小楼",
    "诗词": "漠漠轻寒上小楼，晓阴无赖似穷秋。淡烟流水画屏幽。自在飞花轻似梦，无边丝雨细如愁。宝帘闲挂小银钩。"
} ]);
db.getCollection("ci").insert([ {
    _id: ObjectId("61a8cd8c4a506fc7c2b8c274"),
    "作者名": "李煜",
    "作者朝代": "五代",
    "诗名": "长相思·一重山",
    "诗词": "一重山，两重山。山远天高烟水寒，相思枫叶丹。菊花开，菊花残。塞雁高飞人未还，一帘风月闲。"
} ]);
db.getCollection("ci").insert([ {
    _id: ObjectId("61a8cd8c4a506fc7c2b8c275"),
    "作者名": "纳兰性德",
    "作者朝代": "清代",
    "诗名": "画堂春·一生一代一双人",
    "诗词": "一生一代一双人，争教两处销魂。相思相望不相亲，天为谁春？浆向蓝桥易乞，药成碧海难奔。若容相访饮牛津，相对忘贫。"
} ]);
db.getCollection("ci").insert([ {
    _id: ObjectId("61a8cd8c4a506fc7c2b8c276"),
    "作者名": "韦庄",
    "作者朝代": "唐代",
    "诗名": "菩萨蛮·人人尽说江南好",
    "诗词": "人人尽说江南好，游人只合江南老。春水碧于天，画船听雨眠。垆边人似月，皓腕凝霜雪。未老莫还乡，还乡须断肠。"
} ]);
db.getCollection("ci").insert([ {
    _id: ObjectId("61a8cd8c4a506fc7c2b8c277"),
    "作者名": "欧阳修",
    "作者朝代": "宋代",
    "诗名": "玉楼春·尊前拟把归期说",
    "诗词": "尊前拟把归期说，欲语春容先惨咽。人生自是有情痴，此恨不关风与月。离歌且莫翻新阕，一曲能教肠寸结。直须看尽洛城花，始共春风容易别。"
} ]);
db.getCollection("ci").insert([ {
    _id: ObjectId("61a8cd8c4a506fc7c2b8c278"),
    "作者名": "欧阳修",
    "作者朝代": "宋代",
    "诗名": "浪淘沙·把酒祝东风",
    "诗词": "把酒祝东风，且共从容。垂杨紫陌洛城东。总是当时携手处，游遍芳丛。聚散苦匆匆，此恨无穷。今年花胜去年红。可惜明年花更好，知与谁同？"
} ]);
db.getCollection("ci").insert([ {
    _id: ObjectId("61a8cd8c4a506fc7c2b8c279"),
    "作者名": "李白(存疑)",
    "作者朝代": "唐代",
    "诗名": "忆秦娥·箫声咽",
    "诗词": "箫声咽，秦娥梦断秦楼月。秦楼月，年年柳色，灞陵伤别。乐游原上清秋节，咸阳古道音尘绝。音尘绝，西风残照，汉家陵阙。"
} ]);
db.getCollection("ci").insert([ {
    _id: ObjectId("61a8cd8c4a506fc7c2b8c27a"),
    "作者名": "李清照",
    "作者朝代": "宋代",
    "诗名": "点绛唇·蹴罢秋千",
    "诗词": "蹴罢秋千，起来慵整纤纤手。露浓花瘦，薄汗轻衣透。见客入来，袜刬金钗溜。和羞走，倚门回首，却把青梅嗅。"
} ]);
db.getCollection("ci").insert([ {
    _id: ObjectId("61a8cd8c4a506fc7c2b8c27b"),
    "作者名": "秋瑾",
    "作者朝代": "近现代",
    "诗名": "满江红·小住京华",
    "诗词": "小住京华，早又是中秋佳节。为篱下黄花开遍，秋容如拭。四面歌残终破楚，八年风味徒思浙。苦将侬强派作蛾眉，殊未屑！(徒思浙 一作：独思浙，蛾眉 一作：娥眉)身不得，男儿列，心却比，男儿烈。算平生肝胆，因人常热。俗子胸襟谁识我？英雄末路当磨折。莽红尘何处觅知音？青衫湿！"
} ]);
db.getCollection("ci").insert([ {
    _id: ObjectId("61a8cd8c4a506fc7c2b8c27c"),
    "作者名": "毛泽东",
    "作者朝代": "近现代",
    "诗名": "忆秦娥·娄山关",
    "诗词": "西风烈，长空雁叫霜晨月。霜晨月，马蹄声碎，喇叭声咽。雄关漫道真如铁，而今迈步从头越。从头越，苍山如海，残阳如血。"
} ]);
db.getCollection("ci").insert([ {
    _id: ObjectId("61a8cd8c4a506fc7c2b8c27d"),
    "作者名": "晏几道",
    "作者朝代": "宋代",
    "诗名": "鹧鸪天·彩袖殷勤捧玉钟",
    "诗词": "彩袖殷勤捧玉钟。当年拚却醉颜红。舞低杨柳楼心月，歌尽桃花扇底风。从别后，忆相逢。几回魂梦与君同。今宵剩把银釭照，犹恐相逢是梦中。"
} ]);
db.getCollection("ci").insert([ {
    _id: ObjectId("61a8cd8c4a506fc7c2b8c27e"),
    "作者名": "贺铸",
    "作者朝代": "宋代",
    "诗名": "青玉案·凌波不过横塘路",
    "诗词": "凌波不过横塘路，但目送、芳尘去。锦瑟华年谁与度？月桥花院，琐窗朱户，只有春知处。飞云冉冉蘅皋暮，彩笔新题断肠句。试问闲情都几许？一川烟草，满城风絮，梅子黄时雨。(版本一)凌波不过横塘路，但目送、芳尘去。锦瑟华年谁与度？月台花榭，琐窗朱户，只有春知处。碧云冉冉蘅皋暮，彩笔新题断肠句。试问闲愁都几许？一川烟草，满城风絮，梅子黄时雨。(版本二)"
} ]);
db.getCollection("ci").insert([ {
    _id: ObjectId("61a8cd8c4a506fc7c2b8c27f"),
    "作者名": "蒋捷",
    "作者朝代": "宋代",
    "诗名": "一剪梅·舟过吴江",
    "诗词": "一片春愁待酒浇。江上舟摇，楼上帘招。秋娘渡与泰娘桥，风又飘飘，雨又萧萧。（渡 一作：度。）何日归家洗客袍？银字笙调，心字香烧。流光容易把人抛，红了樱桃，绿了芭蕉。"
} ]);
db.getCollection("ci").insert([ {
    _id: ObjectId("61a8cd8c4a506fc7c2b8c280"),
    "作者名": "周邦彦",
    "作者朝代": "宋代",
    "诗名": "苏幕遮·燎沉香",
    "诗词": "燎沉香，消溽暑。鸟雀呼晴，侵晓窥檐语。叶上初阳干宿雨，水面清圆，一一风荷举。故乡遥，何日去？家住吴门，久作长安旅。五月渔郎相忆否？小楫轻舟，梦入芙蓉浦。"
} ]);
db.getCollection("ci").insert([ {
    _id: ObjectId("61a8cd8c4a506fc7c2b8c281"),
    "作者名": "秦观",
    "作者朝代": "宋代",
    "诗名": "踏莎行·郴州旅舍",
    "诗词": "雾失楼台，月迷津渡。桃源望断无寻处。可堪孤馆闭春寒，杜鹃声里斜阳暮。 驿寄梅花，鱼传尺素。砌成此恨无重数。郴江幸自绕郴山，为谁流下潇湘去。"
} ]);
db.getCollection("ci").insert([ {
    _id: ObjectId("61a8cd8c4a506fc7c2b8c282"),
    "作者名": "李清照",
    "作者朝代": "宋代",
    "诗名": "鹧鸪天·桂花",
    "诗词": "暗淡轻黄体性柔，情疏迹远只香留。何须浅碧深红色，自是花中第一流。(深 一作：轻)梅定妒，菊应羞，画阑开处冠中秋。骚人可煞无情思，何事当年不见收。(阑 通：栏)"
} ]);
db.getCollection("ci").insert([ {
    _id: ObjectId("61a8cd8c4a506fc7c2b8c283"),
    "作者名": "辛弃疾",
    "作者朝代": "宋代",
    "诗名": "贺新郎·甚矣吾衰矣",
    "诗词": "邑中园亭，仆皆为赋此词。一日，独坐停云，水声山色，竞来相娱。意溪山欲援例者，遂作数语，庶几仿佛渊明思亲友之意云。甚矣吾衰矣。怅平生、交游零落，只今余几！白发空垂三千丈，一笑人间万事。问何物、能令公喜？我见青山多妩媚，料青山见我应如是。情与貌，略相似。一尊搔首东窗里。想渊明、《停云》诗就，此时风味。江左沉酣求名者，岂识浊醪妙理。回首叫、云飞风起。不恨古人吾不见，恨古人不见吾狂耳。知我者，二三子。"
} ]);
db.getCollection("ci").insert([ {
    _id: ObjectId("61a8cd8c4a506fc7c2b8c284"),
    "作者名": "辛弃疾",
    "作者朝代": "宋代",
    "诗名": "摸鱼儿·更能消几番风雨",
    "诗词": "淳熙己亥，自湖北漕移湖南，同官王正之置酒小山亭，为赋。更能消、几番风雨，匆匆春又归去。惜春长怕花开早，何况落红无数。春且住，见说道、天涯芳草无归路。怨春不语。算只有殷勤，画檐蛛网，尽日惹飞絮。(怕 一作：恨) 长门事，准拟佳期又误。蛾眉曾有人妒。千金纵买相如赋，脉脉此情谁诉？君莫舞，君不见、玉环飞燕皆尘土！闲愁最苦！休去倚危栏，斜阳正在，烟柳断肠处。 "
} ]);
db.getCollection("ci").insert([ {
    _id: ObjectId("61a8cd8c4a506fc7c2b8c285"),
    "作者名": "晏殊",
    "作者朝代": "宋代",
    "诗名": "破阵子·春景",
    "诗词": "燕子来时新社，梨花落后清明。池上碧苔三四点，叶底黄鹂一两声。日长飞絮轻。巧笑东邻女伴，采桑径里逢迎。疑怪昨宵春梦好，元是今朝斗草赢。笑从双脸生。"
} ]);
db.getCollection("ci").insert([ {
    _id: ObjectId("61a8cd8c4a506fc7c2b8c286"),
    "作者名": "辛弃疾",
    "作者朝代": "宋代",
    "诗名": "太常引·建康中秋夜为吕叔潜赋",
    "诗词": "一轮秋影转金波，飞镜又重磨。把酒问姮娥：被白发，欺人奈何？乘风好去，长空万里，直下看山河。斫去桂婆娑，人道是，清光更多。"
} ]);
db.getCollection("ci").insert([ {
    _id: ObjectId("61a8cd8c4a506fc7c2b8c287"),
    "作者名": "王安石",
    "作者朝代": "宋代",
    "诗名": "桂枝香·金陵怀古",
    "诗词": "登临送目，正故国晚秋，天气初肃。千里澄江似练，翠峰如簇。归帆去棹残阳里，背西风，酒旗斜矗。彩舟云淡，星河鹭起，画图难足。(归帆 一作：征帆)念往昔，繁华竞逐，叹门外楼头，悲恨相续。千古凭高对此，谩嗟荣辱。六朝旧事随流水，但寒烟衰草凝绿。至今商女，时时犹唱，后庭遗曲。(衰草 一作：芳草)"
} ]);
db.getCollection("ci").insert([ {
    _id: ObjectId("61a8cd8c4a506fc7c2b8c288"),
    "作者名": "纳兰性德",
    "作者朝代": "清代",
    "诗名": "蝶恋花·出塞",
    "诗词": "今古河山无定据。画角声中，牧马频来去。满目荒凉谁可语？西风吹老丹枫树。从前幽怨应无数。铁马金戈，青冢黄昏路。一往情深深几许？深山夕照深秋雨。"
} ]);
db.getCollection("ci").insert([ {
    _id: ObjectId("61a8cd8c4a506fc7c2b8c289"),
    "作者名": "陈与义",
    "作者朝代": "宋代",
    "诗名": "临江仙·夜登小阁忆洛中旧游",
    "诗词": "忆昔午桥桥上饮，坐中多是豪英。长沟流月去无声。杏花疏影里，吹笛到天明。 二十余年如一梦，此身虽在堪惊。闲登小阁看新晴。古今多少事，渔唱起三更。"
} ]);
db.getCollection("ci").insert([ {
    _id: ObjectId("61a8cd8c4a506fc7c2b8c28a"),
    "作者名": "王国维",
    "作者朝代": "近现代",
    "诗名": "蝶恋花·阅尽天涯离别苦",
    "诗词": "阅尽天涯离别苦，不道归来，零落花如许。花底相看无一语，绿窗春与天俱莫。(俱莫 一作：俱暮)待把相思灯下诉，一缕新欢，旧恨千千缕。最是人间留不住，朱颜辞镜花辞树。"
} ]);
db.getCollection("ci").insert([ {
    _id: ObjectId("61a8cd8c4a506fc7c2b8c28b"),
    "作者名": "秦观",
    "作者朝代": "宋代",
    "诗名": "行香子·树绕村庄",
    "诗词": "树绕村庄，水满陂塘。倚东风，豪兴徜徉。小园几许，收尽春光。有桃花红，李花白，菜花黄。远远围墙，隐隐茅堂。飏青旗，流水桥旁。偶然乘兴，步过东冈。正莺儿啼，燕儿舞，蝶儿忙。"
} ]);
db.getCollection("ci").insert([ {
    _id: ObjectId("61a8cd8c4a506fc7c2b8c28c"),
    "作者名": "苏轼",
    "作者朝代": "宋代",
    "诗名": "阳关曲·中秋月",
    "诗词": "暮云收尽溢清寒，银汉无声转玉盘。此生此夜不长好，明月明年何处看。"
} ]);
db.getCollection("ci").insert([ {
    _id: ObjectId("61a8cd8c4a506fc7c2b8c28d"),
    "作者名": "温庭筠",
    "作者朝代": "唐代",
    "诗名": "望江南·梳洗罢",
    "诗词": "梳洗罢，独倚望江楼。过尽千帆皆不是，斜晖脉脉水悠悠。肠断白蘋洲。"
} ]);
db.getCollection("ci").insert([ {
    _id: ObjectId("61a8cd8c4a506fc7c2b8c28e"),
    "作者名": "苏轼",
    "作者朝代": "宋代",
    "诗名": "水调歌头·黄州快哉亭赠张偓佺",
    "诗词": "落日绣帘卷，亭下水连空。知君为我新作，窗户湿青红。长记平山堂上，欹枕江南烟雨，杳杳没孤鸿。认得醉翁语，山色有无中。一千顷，都镜净，倒碧峰。忽然浪起，掀舞一叶白头翁。堪笑兰台公子，未解庄生天籁，刚道有雌雄。一点浩然气，千里快哉风。"
} ]);
db.getCollection("ci").insert([ {
    _id: ObjectId("61a8cd8c4a506fc7c2b8c28f"),
    "作者名": "白居易",
    "作者朝代": "唐代",
    "诗名": "长相思·汴水流",
    "诗词": "汴水流，泗水流，流到瓜州古渡头。吴山点点愁。思悠悠，恨悠悠，恨到归时方始休。月明人倚楼。"
} ]);
db.getCollection("ci").insert([ {
    _id: ObjectId("61a8cd8c4a506fc7c2b8c290"),
    "作者名": "苏轼",
    "作者朝代": "宋代",
    "诗名": "行香子·述怀",
    "诗词": "清夜无尘，月色如银。酒斟时、须满十分。浮名浮利，虚苦劳神。叹隙中驹，石中火，梦中身。虽抱文章，开口谁亲。且陶陶、乐尽天真。几时归去，作个闲人。对一张琴，一壶酒，一溪云。"
} ]);

