} ]);
db.getCollection("poem_1").insert([ {
    _id: ObjectId("61a8c85e8cd1d65780b068b4"),
    "作者名": "李白",
    "作者朝代": "唐代",
    "诗名": "赠汪伦",
    "诗词": "李白乘舟将欲行，忽闻岸上踏歌声。桃花潭水深千尺，不及汪伦送我情。 "
} ]);
db.getCollection("poem_1").insert([ {
    _id: ObjectId("61a8c85e8cd1d65780b068b5"),
    "作者名": "杜牧",
    "作者朝代": "唐代",
    "诗名": "秋夕",
    "诗词": "银烛秋光冷画屏，轻罗小扇扑流萤。天阶夜色凉如水，卧看牵牛织女星。(天阶 一作：天街；卧看 一作：坐看)"
} ]);
db.getCollection("poem_1").insert([ {
    _id: ObjectId("61a8c85e8cd1d65780b068b6"),
    "作者名": "李白",
    "作者朝代": "唐代",
    "诗名": "梦游天姥吟留别",
    "诗词": "海客谈瀛洲，烟涛微茫信难求；越人语天姥，云霞明灭或可睹。天姥连天向天横，势拔五岳掩赤城。天台四万八千丈，对此欲倒东南倾。(四万 一作：一万)我欲因之梦吴越，一夜飞度镜湖月。湖月照我影，送我至剡溪。谢公宿处今尚在，渌水荡漾清猿啼。脚著谢公屐，身登青云梯。半壁见海日，空中闻天鸡。千岩万转路不定，迷花倚石忽已暝。熊咆龙吟殷岩泉，栗深林兮惊层巅。云青青兮欲雨，水澹澹兮生烟。列缺霹雳，丘峦崩摧。洞天石扉，訇然中开。青冥浩荡不见底，日月照耀金银台。 霓为衣兮风为马，云之君兮纷纷而来下。虎鼓瑟兮鸾回车，仙之人兮列如麻。忽魂悸以魄动，恍惊起而长嗟。惟觉时之枕席，失向来之烟霞。世间行乐亦如此，古来万事东流水。别君去兮何时还？且放白鹿青崖间，须行即骑访名山。安能摧眉折腰事权贵，使我不得开心颜！"
} ]);
db.getCollection("poem_1").insert([ {
    _id: ObjectId("61a8c85e8cd1d65780b068b7"),
    "作者名": "汉乐府",
    "作者朝代": "两汉",
    "诗名": "长歌行",
    "诗词": "青青园中葵，朝露待日晞。阳春布德泽，万物生光辉。常恐秋节至，焜黄华叶衰。百川东到海，何时复西归？少壮不努力，老大徒伤悲！"
} ]);
db.getCollection("poem_1").insert([ {
    _id: ObjectId("61a8c85e8cd1d65780b068b8"),
    "作者名": "李白",
    "作者朝代": "唐代",
    "诗名": "望天门山",
    "诗词": "天门中断楚江开，碧水东流至此回。两岸青山相对出，孤帆一片日边来。"
} ]);
db.getCollection("poem_1").insert([ {
    _id: ObjectId("61a8c85e8cd1d65780b068b9"),
    "作者名": "杜甫",
    "作者朝代": "唐代",
    "诗名": "闻官军收河南河北",
    "诗词": "剑外忽传收蓟北，初闻涕泪满衣裳。却看妻子愁何在，漫卷诗书喜欲狂。白日放歌须纵酒，青春作伴好还乡。即从巴峡穿巫峡，便下襄阳向洛阳。"
} ]);
db.getCollection("poem_1").insert([ {
    _id: ObjectId("61a8c85e8cd1d65780b068ba"),
    "作者名": "王维",
    "作者朝代": "唐代",
    "诗名": "送元二使安西",
    "诗词": "渭城朝雨浥轻尘，客舍青青柳色新。劝君更尽一杯酒，西出阳关无故人。"
} ]);
db.getCollection("poem_1").insert([ {
    _id: ObjectId("61a8c85e8cd1d65780b068bb"),
    "作者名": "龚自珍",
    "作者朝代": "清代",
    "诗名": "己亥杂诗·其五",
    "诗词": "浩荡离愁白日斜，吟鞭东指即天涯。落红不是无情物，化作春泥更护花。"
} ]);
db.getCollection("poem_1").insert([ {
    _id: ObjectId("61a8c85e8cd1d65780b068bc"),
    "作者名": "杨万里",
    "作者朝代": "宋代",
    "诗名": "晓出净慈寺送林子方",
    "诗词": "毕竟西湖六月中，风光不与四时同。接天莲叶无穷碧，映日荷花别样红。"
} ]);
db.getCollection("poem_1").insert([ {
    _id: ObjectId("61a8c85e8cd1d65780b068bd"),
    "作者名": "孟浩然",
    "作者朝代": "唐代",
    "诗名": "过故人庄",
    "诗词": "故人具鸡黍，邀我至田家。绿树村边合，青山郭外斜。开轩面场圃，把酒话桑麻。待到重阳日，还来就菊花。"
} ]);
db.getCollection("poem_1").insert([ {
    _id: ObjectId("61a8c85e8cd1d65780b068be"),
    "作者名": "李白",
    "作者朝代": "唐代",
    "诗名": "清平调·其一",
    "诗词": "云想衣裳花想容，春风拂槛露华浓。 若非群玉山头见，会向瑶台月下逢。"
} ]);
db.getCollection("poem_1").insert([ {
    _id: ObjectId("61a8c85e8cd1d65780b068bf"),
    "作者名": "王维",
    "作者朝代": "唐代",
    "诗名": "竹里馆",
    "诗词": "独坐幽篁里，弹琴复长啸。深林人不知，明月来相照。"
} ]);
db.getCollection("poem_1").insert([ {
    _id: ObjectId("61a8c85e8cd1d65780b068c0"),
    "作者名": "白居易",
    "作者朝代": "唐代",
    "诗名": "赋得古原草送别",
    "诗词": "离离原上草，一岁一枯荣。野火烧不尽，春风吹又生。远芳侵古道，晴翠接荒城。又送王孙去，萋萋满别情。"
} ]);
db.getCollection("poem_1").insert([ {
    _id: ObjectId("61a8c85e8cd1d65780b068c1"),
    "作者名": "李白",
    "作者朝代": "唐代",
    "诗名": "侠客行",
    "诗词": "赵客缦胡缨，吴钩霜雪明。银鞍照白马，飒沓如流星。十步杀一人，千里不留行。事了拂衣去，深藏身与名。闲过信陵饮，脱剑膝前横。将炙啖朱亥，持觞劝侯嬴。三杯吐然诺，五岳倒为轻。眼花耳热后，意气素霓生。救赵挥金槌，邯郸先震惊。千秋二壮士，烜赫大梁城。纵死侠骨香，不惭世上英。谁能书阁下，白首太玄经。"
} ]);
db.getCollection("poem_1").insert([ {
    _id: ObjectId("61a8c85e8cd1d65780b068c2"),
    "作者名": "王昌龄",
    "作者朝代": "唐代",
    "诗名": "出塞",
    "诗词": "秦时明月汉时关，万里长征人未还。但使龙城飞将在，不教胡马度阴山。"
} ]);
db.getCollection("poem_1").insert([ {
    _id: ObjectId("61a8c85e8cd1d65780b068c3"),
    "作者名": "王安石",
    "作者朝代": "宋代",
    "诗名": "泊船瓜洲",
    "诗词": "京口瓜洲一水间，钟山只隔数重山。春风又绿江南岸，明月何时照我还。"
} ]);
db.getCollection("poem_1").insert([ {
    _id: ObjectId("61a8c85e8cd1d65780b068c4"),
    "作者名": "杜牧",
    "作者朝代": "唐代",
    "诗名": "泊秦淮",
    "诗词": "烟笼寒水月笼沙，夜泊秦淮近酒家。商女不知亡国恨，隔江犹唱后庭花。"
} ]);
db.getCollection("poem_1").insert([ {
    _id: ObjectId("61a8c85e8cd1d65780b068c5"),
    "作者名": "元稹",
    "作者朝代": "唐代",
    "诗名": "离思五首·其四",
    "诗词": "曾经沧海难为水，除却巫山不是云。取次花丛懒回顾，半缘修道半缘君。"
} ]);
db.getCollection("poem_1").insert([ {
    _id: ObjectId("61a8c85e8cd1d65780b068c6"),
    "作者名": "陆游",
    "作者朝代": "宋代",
    "诗名": "示儿",
    "诗词": "死去元知万事空，但悲不见九州同。王师北定中原日，家祭无忘告乃翁。"
} ]);
db.getCollection("poem_1").insert([ {
    _id: ObjectId("61a8c85e8cd1d65780b068c7"),
    "作者名": "杜牧",
    "作者朝代": "唐代",
    "诗名": "江南春",
    "诗词": "千里莺啼绿映红，水村山郭酒旗风。南朝四百八十寺，多少楼台烟雨中。"
} ]);
db.getCollection("poem_1").insert([ {
    _id: ObjectId("61a8c85e8cd1d65780b068c8"),
    "作者名": "李清照",
    "作者朝代": "宋代",
    "诗名": "夏日绝句",
    "诗词": "生当作人杰，死亦为鬼雄。至今思项羽，不肯过江东。"
} ]);
db.getCollection("poem_1").insert([ {
    _id: ObjectId("61a8c85e8cd1d65780b068c9"),
    "作者名": "王之涣",
    "作者朝代": "唐代",
    "诗名": "登鹳雀楼",
    "诗词": "白日依山尽，黄河入海流。欲穷千里目，更上一层楼。"
} ]);
db.getCollection("poem_1").insert([ {
    _id: ObjectId("61a8c85e8cd1d65780b068ca"),
    "作者名": "杜牧",
    "作者朝代": "唐代",
    "诗名": "赤壁",
    "诗词": "折戟沉沙铁未销，自将磨洗认前朝。东风不与周郎便，铜雀春深锁二乔。"
} ]);
db.getCollection("poem_1").insert([ {
    _id: ObjectId("61a8c85e8cd1d65780b068cb"),
    "作者名": "白居易",
    "作者朝代": "唐代",
    "诗名": "卖炭翁",
    "诗词": "苦宫市也卖炭翁，伐薪烧炭南山中。满面尘灰烟火色，两鬓苍苍十指黑。卖炭得钱何所营？身上衣裳口中食。可怜身上衣正单，心忧炭贱愿天寒。夜来城外一尺雪，晓驾炭车辗冰辙。牛困人饥日已高，市南门外泥中歇。翩翩两骑来是谁？黄衣使者白衫儿。手把文书口称敕，回车叱牛牵向北。一车炭，千余斤，宫使驱将惜不得。半匹红纱一丈绫，系向牛头充炭直。(红纱 一作：红绡)"
} ]);
db.getCollection("poem_1").insert([ {
    _id: ObjectId("61a8c85e8cd1d65780b068cc"),
    "作者名": "王维",
    "作者朝代": "唐代",
    "诗名": "鸟鸣涧",
    "诗词": "人闲桂花落，夜静春山空。月出惊山鸟，时鸣春涧中。"
} ]);
db.getCollection("poem_1").insert([ {
    _id: ObjectId("61a8c85e8cd1d65780b068cd"),
    "作者名": "张九龄",
    "作者朝代": "唐代",
    "诗名": "望月怀远",
    "诗词": "海上生明月，天涯共此时。情人怨遥夜，竟夕起相思。灭烛怜光满，披衣觉露滋。不堪盈手赠，还寝梦佳期。"
} ]);
db.getCollection("poem_1").insert([ {
    _id: ObjectId("61a8c85e8cd1d65780b068ce"),
    "作者名": "李贺",
    "作者朝代": "唐代",
    "诗名": "雁门太守行",
    "诗词": "黑云压城城欲摧，甲光向日金鳞开。(向日 一作：向月)角声满天秋色里，塞上燕脂凝夜紫。(塞上 一作：塞土)半卷红旗临易水，霜重鼓寒声不起。报君黄金台上意，提携玉龙为君死。"
} ]);
db.getCollection("poem_1").insert([ {
    _id: ObjectId("61a8c85e8cd1d65780b068cf"),
    "作者名": "李白",
    "作者朝代": "唐代",
    "诗名": "早发白帝城",
    "诗词": "朝辞白帝彩云间，千里江陵一日还。两岸猿声啼不住，轻舟已过万重山。"
} ]);
db.getCollection("poem_1").insert([ {
    _id: ObjectId("61a8c85e8cd1d65780b068d0"),
    "作者名": "杜甫",
    "作者朝代": "唐代",
    "诗名": "蜀相",
    "诗词": "丞相祠堂何处寻？锦官城外柏森森。映阶碧草自春色，隔叶黄鹂空好音。三顾频烦天下计，两朝开济老臣心。出师未捷身先死，长使英雄泪满襟。"
} ]);
db.getCollection("poem_1").insert([ {
    _id: ObjectId("61a8c85e8cd1d65780b068d1"),
    "作者名": "杜甫",
    "作者朝代": "唐代",
    "诗名": "月夜忆舍弟",
    "诗词": "戍鼓断人行，边秋一雁声。(边秋 一作:秋边)露从今夜白，月是故乡明。有弟皆分散，无家问死生。寄书长不达，况乃未休兵。"
} ]);
db.getCollection("poem_1").insert([ {
    _id: ObjectId("61a8c85e8cd1d65780b068d2"),
    "作者名": "苏轼",
    "作者朝代": "宋代",
    "诗名": "卜算子·黄州定慧院寓居作",
    "诗词": "缺月挂疏桐，漏断人初静。谁见幽人独往来，缥缈孤鸿影。(谁见 一作：时见)惊起却回头，有恨无人省。拣尽寒枝不肯栖，寂寞沙洲冷。"
} ]);
db.getCollection("poem_1").insert([ {
    _id: ObjectId("61a8c85e8cd1d65780b068d3"),
    "作者名": "王之涣",
    "作者朝代": "唐代",
    "诗名": "凉州词二首·其一",
    "诗词": "黄河远上白云间，一片孤城万仞山。羌笛何须怨杨柳，春风不度玉门关。"
} ]);
db.getCollection("poem_1").insert([ {
    _id: ObjectId("61a8c85e8cd1d65780b068d4"),
    "作者名": "李白",
    "作者朝代": "唐代",
    "诗名": "古朗月行",
    "诗词": "小时不识月，呼作白玉盘。又疑瑶台镜，飞在青云端。(青云 一作：白云)仙人垂两足，桂树何团团。白兔捣药成，问言与谁餐？蟾蜍蚀圆影，大明夜已残。羿昔落九乌，天人清且安。阴精此沦惑，去去不足观。忧来其如何？凄怆摧心肝。"
} ]);
db.getCollection("poem_1").insert([ {
    _id: ObjectId("61a8c85e8cd1d65780b068d5"),
    "作者名": "李商隐",
    "作者朝代": "唐代",
    "诗名": "无题·昨夜星辰昨夜风",
    "诗词": "昨夜星辰昨夜风，画楼西畔桂堂东。身无彩凤双飞翼，心有灵犀一点通。隔座送钩春酒暖，分曹射覆蜡灯红。嗟余听鼓应官去，走马兰台类转蓬。"
} ]);
db.getCollection("poem_1").insert([ {
    _id: ObjectId("61a8c85e8cd1d65780b068d6"),
    "作者名": "李白",
    "作者朝代": "唐代",
    "诗名": "春夜洛城闻笛",
    "诗词": "谁家玉笛暗飞声，散入春风满洛城。 此夜曲中闻折柳，何人不起故园情。"
} ]);
db.getCollection("poem_1").insert([ {
    _id: ObjectId("61a8c85e8cd1d65780b068d7"),
    "作者名": "陶渊明",
    "作者朝代": "魏晋",
    "诗名": "归园田居·其一",
    "诗词": "少无适俗韵，性本爱丘山。误落尘网中，一去三十年。(误落 一作：误入)羁鸟恋旧林，池鱼思故渊。开荒南野际，守拙归园田。方宅十余亩，草屋八九间。榆柳荫后檐，桃李罗堂前。暧暧远人村，依依墟里烟。狗吠深巷中，鸡鸣桑树颠。户庭无尘杂，虚室有余闲。久在樊笼里，复得返自然。"
} ]);
db.getCollection("poem_1").insert([ {
    _id: ObjectId("61a8c85e8cd1d65780b068d8"),
    "作者名": "郑燮",
    "作者朝代": "清代",
    "诗名": "竹石",
    "诗词": "咬定青山不放松，立根原在破岩中。千磨万击还坚劲，任尔东西南北风。"
} ]);
db.getCollection("poem_1").insert([ {
    _id: ObjectId("61a8c85e8cd1d65780b068d9"),
    "作者名": "王维",
    "作者朝代": "唐代",
    "诗名": "鹿柴",
    "诗词": "空山不见人，但闻人语响。返景入深林，复照青苔上。"
} ]);
db.getCollection("poem_1").insert([ {
    _id: ObjectId("61a8c85e8cd1d65780b068da"),
    "作者名": "杜牧",
    "作者朝代": "唐代",
    "诗名": "山行",
    "诗词": "远上寒山石径斜，白云生处有人家。(生处 一作：深处)停车坐爱枫林晚，霜叶红于二月花。"
} ]);
db.getCollection("poem_1").insert([ {
    _id: ObjectId("61a8c85e8cd1d65780b068db"),
    "作者名": "王昌龄",
    "作者朝代": "唐代",
    "诗名": "从军行七首·其四",
    "诗词": "青海长云暗雪山，孤城遥望玉门关。黄沙百战穿金甲，不破楼兰终不还。"
} ]);
db.getCollection("poem_1").insert([ {
    _id: ObjectId("61a8c85e8cd1d65780b068dc"),
    "作者名": "张继",
    "作者朝代": "唐代",
    "诗名": "枫桥夜泊",
    "诗词": "月落乌啼霜满天，江枫渔火对愁眠。姑苏城外寒山寺，夜半钟声到客船。"
} ]);
db.getCollection("poem_1").insert([ {
