db.getCollection("tune").insert([ {
    _id: ObjectId("61a8e371e29d669821c5ff81"),
    "作者名": "关汉卿",
    "作者朝代": "元代",
    "曲名": "四块玉·闲适",
    "内容": "适意行，安心坐，渴时饮，饥时餐，醉时歌，困来时就向莎茵卧。日月长，天地阔，闲快活！旧酒投，新醅泼，老瓦盆边笑呵呵，共山僧野叟闲吟和。他出一对鸡，我出一个鹅，闲快活！意马收，心猿锁，跳出红尘恶风波，槐阴午梦谁惊破？离了利名场，钻入安乐窝，闲快活！南亩耕，东山卧，世态人情经历多，闲将往事思量过。贤的是他，愚的是我，争甚么？"
} ]);
db.getCollection("tune").insert([ {
