db.getCollection("tune").insert([ {
    _id: ObjectId("61a8e371e29d669821c5ff85"),
    "作者名": "马致远",
    "作者朝代": "元代",
    "曲名": "蟾宫曲·叹世二首",
    "内容": "咸阳百二山河，两字功名，几阵干戈。项废东吴，刘兴西蜀，梦说南柯。韩信功兀的般证果，蒯通言那里是风魔？成也萧何，败也萧何；醉了由他！东篱半世蹉跎，竹里游亭，小宇婆娑。有个池塘，醒时渔笛，醉后渔歌。严子陵他应笑我，孟光台我待学他。笑我如何？倒大江湖，也避风波。"
} ]);
db.getCollection("tune").insert([ {
