db.getCollection("tune").insert([ {
    _id: ObjectId("61a8e371e29d669821c5ffbc"),
    "作者名": "卢挚",
    "作者朝代": "元代",
    "曲名": "节节高·题洞庭鹿角庙壁",
    "内容": "雨晴云散，满江明月。风微浪息，扁舟一叶。半夜心，三生梦，万里别，闷倚篷窗睡些。"
} ]);
db.getCollection("tune").insert([ {
