db.getCollection("tune").insert([ {
    _id: ObjectId("61a8e371e29d669821c5ffbe"),
    "作者名": "周浩",
    "作者朝代": "元代",
    "曲名": "折桂令·题录鬼簿",
    "内容": "想贞元朝士无多，满目江山，日月如梭。上苑繁华，西湖富贵，总付高歌。麒麟冢衣冠坎坷，凤凰台人物蹉跎。生待如何，死待如何？纸上清名，万古难磨。"
} ]);
db.getCollection("tune").insert([ {
    _id: ObjectId("61a8e371e29d669821c5ffbf"),
    "作者名": "佚名",
    "作者朝代": "元代",
    "曲名": "醉太平·堂堂大元",
    "内容": "堂堂大元，奸佞专权。开河变钞祸根源，惹红巾万千。官法滥，刑法重，黎民怨。人吃人，钞买钞，何曾见。贼做官，官做贼，混贤愚。哀哉可怜！"
} ]);
db.getCollection("tune").insert([ {
    _id: ObjectId("61a8e371e29d669821c5ffc0"),
    "作者名": "张可久",
    "作者朝代": "元代",
    "曲名": "清江引·春思",
    "内容": "黄莺乱啼门外柳，雨细清明后。能消几日春，又是相思瘦。梨花小窗人病酒。"
