db.getCollection("tune").insert([ {
    _id: ObjectId("61a8e371e29d669821c5ffc2"),
    "作者名": "卢挚",
    "作者朝代": "元代",
    "曲名": "蟾宫曲·扬州汪右丞席上即事",
    "内容": "江城歌吹风流，雨过平山，月满西楼。几许年华，三生醉梦，六月凉秋。按锦瑟佳人劝酒，卷朱帘齐按凉州。客去还留，云树萧萧，河汉悠悠。"
} ]);
db.getCollection("tune").insert([ {
    _id: ObjectId("61a8e371e29d669821c5ffc3"),
    "作者名": "张岱",
    "作者朝代": "明代",
    "曲名": "陶庵梦忆序",
    "内容": "\\u3000\\u3000陶庵国破家亡，无所归止。披发入山，駴駴为野人。故旧见之，如毒药猛兽，愕窒不敢与接。作《自挽诗》，每欲引决，因《石匮书》未成，尚视息人世。然瓶粟屡罄，不能举火。始知首阳二老，直头饿死，不食周粟，还是后人妆点语也。\\u3000\\u3000饥饿之余，好弄笔墨。因思昔日生长王、谢，颇事豪华，今日罹此果报：以笠报颅，以蒉报踵，仇簪履也；以衲报裘，以苎报絺，仇轻煖也；以藿报肉，以粝报粻，仇甘旨也；以荐报床，以石报枕，仇温柔也；以绳报枢，以瓮报牖，仇爽垲也；以烟报目，以粪报鼻，仇香艳也；以途报足，以囊报肩，仇舆从也。种种罪案，从种种果报中见之。\\u3000\\u3000鸡鸣枕上，夜气方回。因想余生平，繁华靡丽，过眼皆空，五十年来，总成一梦。今当黍熟黄粱，车旋蚁穴，当作如何消受？遥思往事，忆即书之，持问佛前，一一忏悔。不次岁月，异年谱也；不分门类，别《志林》也。偶拈一则，如游旧径，如见故人，城郭人民，翻用自喜。真所谓“痴人前不得说梦”矣。\\u3000\\u3000昔有西陵脚夫为人担酒，失足破其瓮。念无以偿，痴坐伫想曰：“得是梦便好。”一寒士乡试中式，方赴鹿鸣宴，恍然犹意未真，自啮其臂曰：“莫是梦否？”一梦耳，惟恐其非梦，又惟恐其是梦，其为痴人则一也。\\u3000\\u3000余今大梦将寤，犹事雕虫，又是一番梦呓。因叹慧业文人，名心难化，政如邯郸梦断，漏尽钟鸣，卢生遗表，犹思摹榻二王，以流传后世。则其名根一点，坚固如佛家舍利，劫火猛烈，犹烧之不失也。"
} ]);
db.getCollection("tune").insert([ {
    _id: ObjectId("61a8e371e29d669821c5ffc4"),
    "作者名": "孔尚任",
    "作者朝代": "清代",
    "曲名": "折桂令·问秦淮",
    "内容": "问秦淮旧日窗寮，破纸迎风，坏槛当潮，目断魂消。当年粉黛，何处笙箫？罢灯船端阳不闹，收酒旗重九无聊。白鸟飘飘，绿水滔滔，嫩黄花有些蝶飞，新红叶无个人瞧。"
} ]);
db.getCollection("tune").insert([ {
    _id: ObjectId("61a8e371e29d669821c5ffc5"),
    "作者名": "周文质",
    "作者朝代": "元代",
    "曲名": "折桂令·过多景楼",
